class NewYear{constructor(e){this.root=e,e&&this.init()}init(){this.setupTimer()}setupTimer(){createTimer({daySelector:'[data-new-year-timer="days"]',hourSelector:'[data-new-year-timer="hours"]',minuteSelector:'[data-new-year-timer="minutes"]',secondSelector:'[data-new-year-timer="seconds"]',date:"2025-01-12 08:00:00"})}}document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector("body.--new-year");e&&new NewYear(e)});